const texts = [
  $node["Reescrever Roteiro com Gancho Sensacional"].json.message.content,
  $node["Introducción Inspiradora"].json.message.content,
  $node["Declaración de Fe"].json.message.content,
  $node["Oración de Gratitud"].json.message.content,
  $node["Oración por Protección"].json.message.content,
  $node["Oración de Sanación Interior"].json.message.content,
  $node["Oración por Milagros de Dios"].json.message.content,
  $node["Oración de Liberación"].json.message.content,
  $node["Bendición final / CTA"].json.message.content
];

const finalText = texts.join(" ").replace(/\s+/g, " ").trim();

return [{ json: { text: finalText } }];

function generateUUIDv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

return [
  {
    json: {
      uuid: generateUUIDv4()
    }
  }
];
