// Nó “Code” do n8n — gera um script de concatenação para 10 vídeos com prefixo dinâmico
function concatVideos(items) {
  // 1) captura o id dinâmico
  const id = $('Selecionar Próximo Cartão').first().json.id;
  const prefix = `/tmp/${id}`;

  // 2) monta lista de vídeos de /tmp/[AQUI]vídeo1.mp4 até /tmp/[AQUI]vídeo10.mp4
  const videoPaths = [];
  for (let i = 1; i <= 10; i++) {
    videoPaths.push(`${prefix}vídeo${i}.mp4`);
  }

  // 3) define o path de saída dinâmico
  const outputPath = `${prefix}concatenated_output.mp4`;

  // 4) monta o script bash
  const ffmpegScript = `
#!/bin/bash
# Remove lista antiga e cria nova
rm -f concat_list.txt
touch concat_list.txt

# Adiciona cada vídeo à lista
${videoPaths.map(path => `echo "file '${path}'" >> concat_list.txt`).join('\n')}

# Concatena os vídeos em um único arquivo
ffmpeg -y -f concat -safe 0 -i concat_list.txt -c copy ${outputPath}

echo "Vídeo finalizado: ${outputPath}"
`;

  return [
    {
      json: {
        ffmpegScript,
        videoPaths,
        outputPath,
      },
    },
  ];
}

// Executa a função com todos os itens de entrada
return concatVideos($input.all());

// 1) pega a data/hora atual
const now = new Date();

// 2) adiciona 1 dia
now.setDate(now.getDate() + 1);

// 3) formata como "YYYY-MM-DD"
const formatted = now.toISOString().split('T')[0];

// 4) devolve um único item com a nova data
return [
  {
    json: {
      datePlusOne: formatted
    }
  }
];

// Itera sobre cada item recebido
items.forEach(item => {
  const outputArr = item.json.output;
  // Garante que exista o array e o índice 0 com a propriedade seo
  if (Array.isArray(outputArr) && outputArr[0] && typeof outputArr[0].seo === 'string') {
    let text = outputArr[0].seo;
    // remove quebras de linha reais (\r, \n) e literais (\n, \r)
    text = text
      .replace(/[\r\n]+/g, ' ')   // quebras reais
      .replace(/\\[rn]+/g, ' ');  // sequências literais
    // atualiza o valor no JSON de saída
    outputArr[0].seo = text;
  }
});

// Retorna os items já limpos
return items;
